export default function AttendanceCard() {
  const subjects = [
    { 
      name: "Data Structures", 
      percentage: 85, 
      attended: 34, 
      total: 40,
      nextClass: "Tomorrow 10 AM",
      safeToBunk: true
    },
    { 
      name: "Operating Systems", 
      percentage: 72, 
      attended: 25, 
      total: 35,
      nextClass: "Today 2 PM",
      safeToBunk: false
    }
  ]

  return (
    <div className="bg-white p-6 rounded-lg shadow-sm border border-gray-100">
      <h2 className="text-lg font-bold mb-4">Attendance Overview</h2>
      
      <div className="space-y-4">
        {subjects.map((subject, index) => (
          <div key={index}>
            <div className="flex justify-between items-center mb-1">
              <span className="font-medium">{subject.name}</span>
              <span className={`px-2 py-1 text-xs rounded-full ${
                subject.safeToBunk ? 'bg-green-100 text-green-800' : 'bg-yellow-100 text-yellow-800'
              }`}>
                {subject.percentage}%
              </span>
            </div>
            
            <div className="w-full bg-gray-200 rounded-full h-2 mb-2">
              <div 
                className={`h-2 rounded-full ${
                  subject.safeToBunk ? 'bg-green-500' : 'bg-yellow-500'
                }`}
                style={{ width: `${subject.percentage}%` }}
              />
            </div>
            
            <div className="flex justify-between text-sm text-gray-500">
              <span>{subject.attended}/{subject.total} classes</span>
              <span>Next: {subject.nextClass}</span>
            </div>
          </div>
        ))}
      </div>

      {subjects.some(s => s.safeToBunk) && (
        <div className="mt-6 p-3 bg-blue-50 rounded-lg">
          <p className="text-sm font-medium text-blue-800">
            <span className="font-bold">All Says:</span> Safe to bunk! You can miss 1-2 classes and still maintain 75%
          </p>
        </div>
      )}
    </div>
  )
}
