import { useEffect, useState } from 'react';
import AttendanceCard from '../components/cards/AttendanceCard';
import ActivityCard from '../components/cards/ActivityCard';
import StatCard from '../components/stats/StatCard';
import { getAttendanceSummary } from '../api/attendance';
import {
  AcademicCapIcon,
  ChartBarIcon,
  ShareIcon,
  UsersIcon
} from '@heroicons/react/24/outline';

export default function Dashboard() {
  const [attendance, setAttendance] = useState(null);

  useEffect(() => {
    const fetchAttendance = async () => {
      try {
        const summary = await getAttendanceSummary();
        setAttendance(summary.overallPercentage); // expected field from backend
      } catch (error) {
        console.error('Failed to load attendance summary:', error);
      }
    };

    fetchAttendance();
  }, []);

  return (
    <div className="p-6 max-w-7xl mx-auto">
      <h1 className="text-2xl font-bold mb-2">Welcome back, Student!</h1>
      <p className="text-gray-600 mb-6">Here's your academic overview for today</p>

      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-4 mb-8">
        <StatCard 
          icon={<ChartBarIcon className="h-5 w-5" />}
          title="Overall Attendance"
          value={attendance !== null ? `${attendance}%` : 'Loading...'}
          change="+3% from last week"
          color="blue"
        />
        <StatCard 
          icon={<AcademicCapIcon className="h-5 w-5" />}
          title="XP Points"
          value="1,247"
          change="+125 this week"
          color="purple"
        />
        <StatCard 
          icon={<ShareIcon className="h-5 w-5" />}
          title="Notes Shared"
          value="23"
          change="+5 this month"
          color="green"
        />
        <StatCard 
          icon={<UsersIcon className="h-5 w-5" />}
          title="Study Sessions"
          value="8"
          change="+2 this week"
          color="orange"
        />
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        <AttendanceCard />
        <ActivityCard />
      </div>
    </div>
  );
}
