export default function AttendanceCard() {
  return (
    <div className="bg-white p-4 sm:p-6 rounded-lg shadow-sm border border-gray-100">
      <h2 className="text-lg sm:text-xl font-bold mb-4">Attendance Overview</h2>
      
      {/* Responsive progress bars */}
      <div className="space-y-3 sm:space-y-4">
        {subjects.map((subject) => (
          <div key={subject.name}>
            <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between mb-1">
              <span className="text-sm sm:text-base font-medium">{subject.name}</span>
              <span className={`text-xs sm:text-sm px-2 py-1 rounded-full ${
                subject.percentage >= 75 ? 'bg-green-100 text-green-800' : 'bg-yellow-100 text-yellow-800'
              }`}>
                {subject.percentage}%
              </span>
            </div>
            
            {/* Progress bar */}
            <div className="w-full bg-gray-200 rounded-full h-2 sm:h-2.5 mb-2">
              <div 
                className={`h-full rounded-full ${
                  subject.percentage >= 75 ? 'bg-green-500' : 'bg-yellow-500'
                }`}
                style={{ width: `${subject.percentage}%` }}
              />
            </div>
            
            {/* Bottom row - stacks on mobile */}
            <div className="flex flex-col sm:flex-row sm:justify-between text-xs sm:text-sm text-gray-500">
              <span>{subject.classes} classes</span>
              <span>Next: {subject.nextClass}</span>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
