import { createContext, useContext, useState, useEffect } from 'react';

export const AuthContext = createContext();
export const useAuth = () => useContext(AuthContext);

export default function AuthProvider({ children }) {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Simulate fetching existing session
    const storedUser = JSON.parse(localStorage.getItem('hostelhub-user'));
    if (storedUser) setUser(storedUser);
    setLoading(false);
  }, []);

  const login = (email, password) => {
  setLoading(true);
  return new Promise((resolve) => {
    setTimeout(() => {
      const userData = {
        id: 1,
        name: 'Test User',
        email,
      };
      setUser(userData);
      localStorage.setItem('hostelhub-user', JSON.stringify(userData));

      // Save dummy token for API auth
      localStorage.setItem('token', 'dummy-auth-token');

      setLoading(false);
      resolve();
    }, 1000);
  });
};

const logout = () => {
  setUser(null);
  localStorage.removeItem('hostelhub-user');
  localStorage.removeItem('token'); // remove token on logout
};


  return (
    <AuthContext.Provider value={{ user, loading, login, logout }}>
      {children}
    </AuthContext.Provider>
  );
}
