import { useState } from 'react';
import { Link } from 'react-router-dom';
import { Bars3Icon, XMarkIcon } from '@heroicons/react/24/outline';

export default function Navbar() {
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);

  return (
    <nav className="bg-white shadow-sm">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between h-16">
          {/* Logo - always visible */}
          <Link to="/" className="flex-shrink-0 flex items-center text-xl font-bold text-blue-600">
            HostelHub+
          </Link>

          {/* Desktop Navigation - hidden on mobile */}
          <div className="hidden md:flex space-x-4">
            <Link to="/attendance" className="px-3 py-2 text-gray-700 hover:text-blue-600">
              Attendance
            </Link>
            <Link to="/study-buddies" className="px-3 py-2 text-gray-700 hover:text-blue-600">
              Study Buddies
            </Link>
          </div>

          {/* Mobile menu button - visible only on mobile */}
          <div className="md:hidden flex items-center">
            <button
              onClick={() => setMobileMenuOpen(!mobileMenuOpen)}
              className="inline-flex items-center justify-center p-2 rounded-md text-gray-700 hover:text-blue-600 focus:outline-none"
            >
              {mobileMenuOpen ? (
                <XMarkIcon className="block h-6 w-6" />
              ) : (
                <Bars3Icon className="block h-6 w-6" />
              )}
            </button>
          </div>
        </div>
      </div>

      {/* Mobile menu - appears when hamburger is clicked */}
      {mobileMenuOpen && (
        <div className="md:hidden bg-white border-t">
          <div className="pt-2 pb-3 space-y-1">
            <Link
              to="/attendance"
              className="block px-3 py-2 text-gray-700 hover:text-blue-600 hover:bg-gray-50"
              onClick={() => setMobileMenuOpen(false)}
            >
              Attendance
            </Link>
            <Link
              to="/study-buddies"
              className="block px-3 py-2 text-gray-700 hover:text-blue-600 hover:bg-gray-50"
              onClick={() => setMobileMenuOpen(false)}
            >
              Study Buddies
            </Link>
          </div>
        </div>
      )}
    </nav>
  );
}
