export default function ActivityCard() {
  const activities = [
    {
      type: "Data Structures Notes",
      detail: "Sarah uploaded new notes for Trees and Graphs",
      time: "2 hours ago"
    },
    {
      type: "Operating Systems Help",
      detail: "Alea asked about process synchronization",
      time: "4 hours ago"
    }
  ];

  return (
    <div className="bg-white p-6 rounded-lg shadow border border-gray-100">
      <h2 className="text-xl font-bold mb-4">Recent Activity</h2>
      
      <div className="space-y-4">
        {activities.map((activity, index) => (
          <div key={index} className="border-b pb-4 last:border-b-0 last:pb-0">
            <h3 className="font-medium text-gray-800">{activity.type}</h3>
            <p className="text-gray-600 mt-1">{activity.detail}</p>
            <p className="text-sm text-gray-400 mt-2">{activity.time}</p>
          </div>
        ))}
      </div>
    </div>
  );
}
