export default function Dashboard() {
  return (
    <div className="p-4 sm:p-6">
      {/* Header with responsive text sizing */}
      <div className="mb-6">
        <h1 className="text-xl sm:text-2xl font-bold text-gray-900">Welcome back, Student!</h1>
        <p className="text-sm sm:text-base text-gray-600">Here's your academic overview for today</p>
      </div>

      {/* Stats grid - changes columns based on screen size */}
      <div className="grid grid-cols-2 md:grid-cols-4 gap-3 sm:gap-4 mb-6">
        {/* Stat cards will automatically adjust */}
      </div>

      {/* Main content - stacks on mobile, side-by-side on desktop */}
      <div className="flex flex-col lg:flex-row gap-4 sm:gap-6">
        <div className="flex-1">
          <AttendanceCard />
        </div>
        <div className="flex-1">
          <ActivityCard />
        </div>
      </div>
    </div>
  );
}
