import AttendanceCard from '../components/cards/AttendanceCard';
import ActivityCard from '../components/cards/ActivityCard';
import StatCard from '../components/stats/StatCard';
import { 
  AcademicCapIcon, 
  ChartBarIcon, 
  ShareIcon, 
  UsersIcon,
  HomeIcon,
  BookOpenIcon,
  CalendarIcon,
  TrophyIcon
} from '@heroicons/react/24/outline';
import { Link } from 'react-router-dom';

export default function Dashboard() {
  return (
    <div className="max-w-7xl mx-auto">
      {/* Single Navigation Bar - Keep only this one */}
      <div className="bg-white shadow-sm sticky top-0 z-10 px-6 py-3 flex justify-between items-center">
        <h1 className="text-xl font-bold text-blue-600">HostelHub+</h1>
        <div className="flex space-x-1">
          <NavButton to="/" icon={<HomeIcon className="h-5 w-5" />} text="Dashboard" active />
          <NavButton to="/resources" icon={<BookOpenIcon className="h-5 w-5" />} text="Resources" />
          <NavButton to="/study-buddies" icon={<UsersIcon className="h-5 w-5" />} text="Buddies" />
          <NavButton to="/schedule" icon={<CalendarIcon className="h-5 w-5" />} text="Schedule" />
          <NavButton to="/leaderboard" icon={<TrophyIcon className="h-5 w-5" />} text="Leaderboard" />
        </div>
      </div>

      {/* Dashboard Content */}
      <div className="p-6">
        <h1 className="text-2xl font-bold mb-2">Welcome back, Student!</h1>
        <p className="text-gray-600 mb-6">Here's your academic overview for today</p>

        {/* Stats Grid */}
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-4 mb-8">
          <StatCard 
            icon={<ChartBarIcon className="h-5 w-5" />}
            title="Overall Attendance"
            value="82%"
            change="+3% from last week"
            color="blue"
          />
          <StatCard 
            icon={<AcademicCapIcon className="h-5 w-5" />}
            title="XP Points"
            value="1,247"
            change="+125 this week"
            color="purple"
          />
          <StatCard 
            icon={<ShareIcon className="h-5 w-5" />}
            title="Notes Shared"
            value="23"
            change="+5 this month"
            color="green"
          />
          <StatCard 
            icon={<UsersIcon className="h-5 w-5" />}
            title="Study Sessions"
            value="8"
            change="+2 this week"
            color="orange"
          />
        </div>

        {/* Main Content */}
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
          <AttendanceCard />
          <ActivityCard />
        </div>
      </div>
    </div>
  );
}

// Navigation Button Component
function NavButton({ to, icon, text, active = false }) {
  return (
    <Link
      to={to}
      className={`flex items-center px-3 py-2 rounded-md transition-colors text-sm font-medium ${
        active 
          ? 'text-blue-600 bg-blue-50' 
          : 'text-gray-700 hover:text-blue-600 hover:bg-blue-50'
      }`}
    >
      <span className="mr-2">{icon}</span>
      {text}
    </Link>
  );
}
